select count(*) from ( select e.data_src_code sourceTableCode, e.data_src_tab_name sourceTableName, e.table_data_cycle dataCycle, max(f.data_source_time) dataDate from ( select label_id from ci_label_info where data_status_id=2 and parent_id=-1 union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1))) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)))) ) a left join ci_label_ext_info b on a.label_id=b.label_id left join dim_coc_label_count_rules c on b.count_rules_code=c.count_rules_code left join dim_coc_index_info d on c.depend_index=d.index_code left join dim_coc_index_table_info e on d.data_src_code=e.data_src_code left join dim_coc_data_source_status f on e.data_src_code=f.data_source_id where e.data_src_tab_name like '%'||#{sourceTableName}||'%'
and e.table_data_cycle=#{dataCycle}
group by e.data_src_code,e.data_src_tab_name,e.table_data_cycle )

select sourceTableCode, sourceTableName, dataCycle, dataDate from ( select sourceTableCode, sourceTableName, dataCycle, dataDate, row_number() over(order by dataCycle,dataDate) row_number from ( select e.data_src_code sourceTableCode, e.data_src_tab_name sourceTableName, e.table_data_cycle dataCycle, max(f.data_source_time) dataDate from ( select label_id from ci_label_info where data_status_id=2 and parent_id=-1 union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1))) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)))) ) a left join ci_label_ext_info b on a.label_id=b.label_id left join dim_coc_label_count_rules c on b.count_rules_code=c.count_rules_code left join dim_coc_index_info d on c.depend_index=d.index_code left join dim_coc_index_table_info e on d.data_src_code=e.data_src_code left join dim_coc_data_source_status f on e.data_src_code=f.data_source_id where e.data_src_tab_name like '%'||#{sourceTableName}||'%'
and e.table_data_cycle=#{dataCycle}
group by e.data_src_code,e.data_src_tab_name,e.table_data_cycle ) ) where row_number between #{page.startPosition} and #{page.endPosition}

SELECT count(1) FROM syscat.tables WHERE 1=1
and tabschema=#{tabschema}
and tabname=#{tabname}

SELECT COUNT(*) from ( select label_id from ci_label_info where data_status_id=2 and parent_id=-1 union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1))) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)))) )a left join ci_label_info f on a.label_id=f.label_id LEFT JOIN ci_label_ext_info b on a.label_id=b.label_id left join dim_coc_label_count_rules c on b.count_rules_code=c.count_rules_code left join dim_coc_index_info d on c.depend_index=d.index_code left join dim_coc_index_table_info e on d.data_src_code=e.data_src_code left join dim_coc_data_source_status g on e.data_src_code=g.data_source_id WHERE 1=1 and f.label_name like '%'||#{labelName}||'%'
and f.update_cycle=#{dataCycle}
and b.IS_STAT_USER_NUM = 1

SELECT labelId, labelName, dataCycle, dataDate, useTimes, srcTabName, srcTabCode, srcTabColName, customNum FROM ( SELECT labelId, labelName, dataCycle, dataDate, useTimes, srcTabName, srcTabCode, srcTabColName, customNum, row_number() over(order by srcTabCode DESC ,labelId ASC ) AS row_number FROM ( select a.label_id AS labelId, f.label_name AS labelName, f.update_cycle AS dataCycle, h.use_times AS useTimes, e.data_src_tab_name AS srcTabName, e.data_src_code as srcTabCode, d.data_src_col_name AS srcTabColName, j.custom_num AS customNum, f.data_date AS dataDate from ( select label_id from ci_label_info where data_status_id=2 and parent_id=-1 union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1))) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)))) )a left join ci_label_info f on a.label_id=f.label_id LEFT JOIN ci_label_ext_info b on a.label_id=b.label_id left join dim_coc_label_count_rules c on b.count_rules_code=c.count_rules_code left join dim_coc_index_info d on c.depend_index=d.index_code left join dim_coc_index_table_info e on d.data_src_code=e.data_src_code left join (select label_id,count(*) as use_times from ci_user_use_label group by label_id) h on a.label_id=h.label_id left join (select label_id,sum(custom_num) AS custom_num from ci_label_stat_dm_${opTime} where city_id=-1 and vip_level_id=-1 and brand_id=-1
and data_date=#{dataDate}
group by label_id) j on a.label_id=j.label_id WHERE f.label_name like '%'||#{labelName}||'%'
and f.update_cycle=#{dataCycle}
and b.IS_STAT_USER_NUM = 1 ) ) WHERE row_number BETWEEN #{page.startPosition} AND #{page.endPosition}

select sum(custom_num) AS custom_num from ci_label_stat_dm_${opTime}
where city_id=-1 and vip_level_id=-1 and brand_id=-1

    and data_date=#{dataDate}


    and label_id=#{labelId}

SELECT
count(1)
FROM
(SELECT
1 FROM ci_label_stat_dm_${opTime}
WHERE 1=1

    AND data_date=#{dataDate}


    AND label_id=#{labelId}

FETCH FIRST 1 ROWS ONLY )

select ring_num AS ringNum from ci_label_stat_dm_${opTime}
where city_id=-1 and vip_level_id=-1 and brand_id=-1

    and data_date=#{dataDate}


    and label_id=#{labelId}
