mapper.select:
select count(*) from ( select e.data_src_code sourceTableCode, e.data_src_tab_name sourceTableName, e.table_data_cycle dataCycle, max(data_source_time) dataDate from ( select label_id from ci_label_info where data_status_id=2 and parent_id=-1 union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1))) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)))) ) a left join ci_label_ext_info b on a.label_id=b.label_id left join dim_coc_label_count_rules c on b.count_rules_code=c.count_rules_code left join dim_coc_index_info d on c.depend_index=d.index_code left join dim_coc_index_table_info e on d.data_src_code=e.data_src_code left join dim_coc_data_source_status f on e.data_src_code=f.data_source_id where e.data_src_tab_name like '%'||#{sourceTableName}||'%'
and e.table_data_cycle=#{dataCycle}
group by e.data_src_code,e.data_src_tab_name,e.table_data_cycle )

select sourceTableCode, sourceTableName, dataCycle, dataDate from ( select sourceTableCode, sourceTableName, dataCycle, dataDate, row_number() over(order by dataCycle,sourceTableCode desc) row_number from ( select e.data_src_code sourceTableCode, e.data_src_tab_name sourceTableName, e.table_data_cycle dataCycle, max(data_source_time) dataDate from ( select label_id from ci_label_info where data_status_id=2 and parent_id=-1 union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1))) union all select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id in (select label_id from ci_label_info where data_status_id=2 and parent_id=-1)))) ) a left join ci_label_ext_info b on a.label_id=b.label_id left join dim_coc_label_count_rules c on b.count_rules_code=c.count_rules_code left join dim_coc_index_info d on c.depend_index=d.index_code left join dim_coc_index_table_info e on d.data_src_code=e.data_src_code left join dim_coc_data_source_status f on e.data_src_code=f.data_source_id where e.data_src_tab_name like '%'||#{sourceTableName}||'%'
and e.table_data_cycle=#{dataCycle}
group by e.data_src_code,e.data_src_tab_name,e.table_data_cycle ) ) where row_number between #{page.startPosition} and #{page.endPosition}

SELECT count(1) FROM syscatTables -- syscat.tables WHERE 1=1
and tabschema=#{tabschema}
and tabname=#{tabname}

SELECT count(*) FROM ci_label_info WHERE 1=1
and update_cycle = #{dataCycle}
and data_status_id = 2
